import { React, useState } from "react";
import Validators from "./validators";
import Label from "./label";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
	faAlignLeft,
	faEdit,
	faEraser,
	faTrash,
	faWindowClose,
} from "@fortawesome/free-solid-svg-icons";
import { cover_image_1 } from "./coverImages/cover_image_1";
import { cover_image_2 } from "./coverImages/cover_image_2";
import { cover_image_3 } from "./coverImages/cover_image_3";
import { cover_image_4 } from "./coverImages/cover_image_4";
import { cover_image_5 } from "./coverImages/cover_image_5";
import { cover_image_6 } from "./coverImages/cover_image_6";
import { cover_image_7 } from "./coverImages/cover_image_7";
import { cover_image_8 } from "./coverImages/cover_image_8";
import { cover_image_9 } from "./coverImages/cover_image_9";
import { cover_image_10 } from "./coverImages/cover_image_10";

const style = {
	width: "100%",
};
const overlayStyle = {
	top: 0,
	right: 0,
	bottom: 0,
	left: 0,
	backgroundColor: " rgba(0,0,0,.8)",
	zIndex: 98,
	visibility: "hidden",
};
const editPaneStyle = {
	visibility: "hidden",
	position: "absolute",
	width: "200px",
	backgroundColor: "white",
	padding: "20px",
};
const coverStyle = {
	width: "100%",
	height: "10rem",
	margin: 0,
	padding: 0,
	backgroundRepeat: "no-repeat",
	backgroundPosition: "center center",
	backgroundSize: "cover",
};
const Card = (props) => {
	const [add, setadd] = useState(false || props.add);
	const [showAddButton, setshowAddButton] = useState(false);
	const [currentCard, setcurrentCard] = useState("");
	const [edit, setedit] = useState(false); // true to show modal, false to hide
	const [newCard, setnewCard] = useState({
		value: "",
		checklists: [],
		labels: [],
		dueDate: "",
		description: "",
		comments: [],
		cover: "",
		attachment: "",
		groupKey: props.group.key,
	});

	const handleAddChange = (event) => {
		//update newCard
		if (Validators.isEmpty(event.target.value) === false) {
			setnewCard({
				value: event.target.value,
				checklists: [],
				labels: [],
				dueDate: "",
				description: "",
				comments: [],
				cover: "",
				attachment: "",
				groupKey: props.group.key,
			});
			setshowAddButton(true);
		}
	};

	const handleAddSubmit = (event) => {
		//add newCard into cards and update cards in Group
		event.preventDefault();
		if (Validators.isEmpty(event.target.firstElementChild.value) === false) {
			//props.group.cards.push(newCard);
			setnewCard({
				value: "",
				checklists: [],
				labels: [],
				dueDate: "",
				description: "",
				comments: [],
				cover: "",
				attachment: "",
				groupKey: "",
			});
			setshowAddButton(false);
			props.onCardsChange(props.group, newCard);
		}
	};

	/*
	 *--------------------------edit card ( show modal )--------------------------------
	 */
	const popupEdit = (card) => {
		this.setState({ edit: true, currentCard: card });
	};

	if (add)
		return (
			<>
				{!showAddButton && (
					<p
						className="addcardtxt"
						onClick={() => {
							setshowAddButton(true);
						}}
					>
						&#43; Add another card
					</p>
				)}
				{showAddButton && (
					<form onSubmit={handleAddSubmit}>
						<input
							type="text"
							placeholder="Enter card name"
							onChange={handleAddChange}
							defaultValue=""
						></input>
						<br />
						<button type="submit" className="btn btn-success btn-sm">
							Add
						</button>
					</form>
				)}
			</>
		);
	else
		return (
			<>
				<div style={style} onClick={() => popupEdit(props.card)}>
					<p style={coverStyle}></p>
					{/* <Label smallLabel={true} card={props.card} /> */}
					<div className="d-flex">
						<p className="card-cardname">{props.card.value}</p>
						<span
							className="card-icons-hide"
							data-toggle="tooltip"
							data-html="true"
							title={props.card.description}
						>
							{/* <FontAwesomeIcon icon={faAlignLeft} /> */}
						</span>
						<span className="card-icons-hide">
							{/* <FontAwesomeIcon icon={faEdit} onClick={showEditPane} /> */}
						</span>
					</div>

					{props.card.checklists.map((checklist, index) => {
						return <p>checklist</p>;
					})}
				</div>
			</>

			// {props.card.checklists.map((checklist, index) => {
			// 	return <p key={index}>checklist</p>;
			// })}
		);
};

export default Card;
