import { React, useState } from "react";
import Validators from "./validators";

const Card = (props) => {
	const [showAddButton, setshowAddButton] = useState(false);
	const [currentCard, setcurrentCard] = useState("");
	const [newCard, setnewCard] = useState({
		value: "",
		checklists: [],
		labels: [],
		dueDate: "",
		description: "",
		comments: [],
		cover: "",
		attachment: "",
		groupKey: this.props.group.key,
	});

	const handleAddChange = (event) => {
		//update newCard
		if (Validators.isEmpty(event.target.value) === false) {
			setnewCard({
				value: event.target.value,
				checklists: [],
				labels: [],
				dueDate: "",
				description: "",
				comments: [],
				cover: "",
				attachment: "",
				groupKey: props.group.key,
			});
			setshowAddButton(true);
		}
	};

	const handleAddSubmit = (event) => {
		//add newCard into cards and update cards in Group
		event.preventDefault();
		if (Validators.isEmpty(event.target.firstElementChild.value) === false) {
			props.group.cards.push(this.state.newCard);
			setnewCard({
				value: "",
				checklists: [],
				labels: [],
				dueDate: "",
				description: "",
				comments: [],
				cover: "",
				attachment: "",
				groupKey: "",
			});
			setshowAddButton(false);
			props.onCardsChange();
		}
	};
	if (add)
		return (
			<>
				{!showAddButton && (
					<p
						className="addcardtxt"
						onClick={() => {
							setshowAddButton(false);
						}}
					>
						&#43; Add another card
					</p>
				)}
				{showAddButton && (
					<form onSubmit={handleAddSubmit}>
						<input
							type="text"
							placeholder="Enter card name"
							onChange={handleAddChange}
							defaultValue=""
						></input>
						<br />
						<button type="submit" className="btn btn-success btn-sm">
							Add
						</button>
					</form>
				)}
			</>
		);
	else
		return (
			<div>
				<p></p>
				<div className="d-flex">
					<p className="card-cardname">{props.card.value}</p>
					<span
						className="card-icons-hide"
						data-toggle="tooltip"
						data-html="true"
						title={props.card.description}
					></span>
				</div>

				{props.card.checklists.map((checklist, index) => {
					return <p key={index}>checklist</p>;
				})}
			</div>
		);
};

export default Card;
