import AllCards from "./AllCards";
const Group = (props) => {
	return (
		<>
			{props.add && (
				<form onSubmit={addGroupSubmit}>
					<input type="text" ref={this.groupNameInput} defaultValue=""></input>
					<br />
					<button type="submit" className="btn btn-success btn-sm">
						Add
					</button>
				</form>
			)}
			<AllCards key={props.group.key} group={props.group} />
		</>
	);
};

export default Group;
