import { React, useState, useEffect } from "react";
import Card from "./Card";
import axios from "axios";

const AllCards = (props) => {
	const [allCards, setAllCards] = useState([]);

	useEffect(() => {
		let config = {
			headers: {
				"Content-Type": "application/json",
			},
		};
		let data = {
			groupId: "6075a3c80f096601ccb9c0e1",
		};
		// axios({
		// 	method: "post",
		// 	url: "http://localhost:5000/api/task/card/",
		// 	data,
		// 	config,
		// }).then((res) => console.log(res.data));

		var tempCards = [];
		axios
			.post("http://localhost:5000/api/task/card/", data, config)
			.then((response) => {
				//console.log(response.data);
				let temp = response.data;
				temp.map((card, index) => {
					let c = {
						value: card.value,
						checklists: card.checklists,
						labels: card.labels,
						dueDate: card.dueDate,
						description: card.description,
						comments: card.comments,
						cover: card.cover,
						attachment: card.attachment,
						groupKey: card.group,
						cardId: card._id,
					};
					tempCards = [...tempCards, c];
					console.log(tempCards);
				});
				setAllCards(tempCards);
			});
	}, []);

	const group = props.group;
	return group.cards.map((card, index) => (
		<Card
			key={index}
			index={index}
			card={card}
			group={group}
			onCardsChange={props.onCardsChange}
		/>
	));
};

export default AllCards;
