import React from "react";
import Card from "./Card";

const AllCards = (props) => {
	const [allCards, setAllCards] = useState([]);

	useEffect(() => {
		let config = {
			headers: {
				"Content-Type": "application/json",
			},
			data: {
				groupId: "6075a3c80f096601ccb9c0e1",
			},
		};

		axios
			.get("http://localhost:5000/api/task/card", config)
			.then((response) => {
				//console.log(response.data);
				let temp = response.data;
				temp.map((card, index) => {
					let c = {
						value: temp.value,
						checklists: temp.checklists,
						labels: temp.labels,
						dueDate: temp.dueDate,
						description: temp.description,
						comments: temp.comments,
						cover: temp.cover,
						attachment: temp.attachment,
						groupKey: temp.group,
						cardId: temp._id,
					};
					setAllCards([...allCards, c]);
				});
			});
	}, []);

	const group = props.group;
	return group.cards.map((card, index) => (
		<Card
			key={index}
			index={index}
			card={card}
			group={group}
			onCardsChange={props.onCardsChange}
		/>
	));
};

export default AllCards;
